import './App.css';
import Form from './formulario/Formulario';
import Listar from './listagem/List';
import {useEffect, useState} from 'react';
function App() {

  // UseState
const [btnCadastrar, setBtnCadastrar] = useState(true);
const [ssd, setSsds] = useState([]);

// UseEffect
useEffect(()=>{
fetch("http://localhost:9090/ssd/list")
.then(retorno => retorno.json())
.then(retorno_convertido => setSsds(retorno_convertido));

}, []); // as chaves vão permitir fazer apenas uma requisição. Se não tiver, entra em loop.

// Retorno
  return (
    <div className="App">
  <h3>Cadastro de produtos: SSD</h3> 
<div>  
  <p>{JSON.stringify(ssd)}</p>
      <Form></Form>

  <Form botao={btnCadastrar}/>
        <Listar/>

</div>
      {/* <h3>Produtos cadastrados</h3> */}

    </div>
  );
}
export default App;
