import "./App.css";
import Formulario from "./formulario/Formulario";
import List from "./listagem/List";
import { useEffect, useState } from "react";
import axios from "axios";
import Upload from "./formulario/Upload";

  const url = 'http://localhost:9090/ssd/new';

const App = () => {
    const [brand, setBrand] = useState('');
    const [model, setModel] = useState('');
    const [serialNumber, setSerialNumber] = useState("");
    const [size, setSize] = useState("");
    const [purchaseDate, setPurchaseDate] = useState("");
    const [purchasePrice, setPurchasePrice] = useState("");
    const [arrivalDate, setArrivalDate] = useState("");
    const [productCategory, setProductCategory] = useState("");

 const [files, setFiles] = useState("");

 //state for checking file size
 const [fileSize, setFileSize] = useState(true);

 // for file upload progress message
 const [fileUploadProgress, setFileUploadProgress] = useState(false);

 //for displaying response message
 const [fileUploadResponse, setFileUploadResponse] = useState(null);

 //base end point url
 const FILE_UPLOAD_BASE_ENDPOINT = "http://localhost:9090";

 const uploadFileHandler = (event) => {
   setFiles(event.target.files);
 };
  const fileSubmitHandler = (event) => {
    event.preventDefault();
    setFileSize(true);
    setFileUploadProgress(true);
    setFileUploadResponse(null);
    setBrand();
    setModel();
    setSize();
    setSerialNumber();

    const formData = new FormData();

    // for (let i = 0; i < files.length; i++) {
    //   if (files[i].size > 1024) {
    //     setFileSize(false);
    //     setFileUploadProgress(false);
    //     setFileUploadResponse(null);
    //     return;
    //   }
    //   formData.append(`files`, files[i]);
    // }

    const requestOptions = {
      method: "POST",
      body: formData,
    };
    axios.post(FILE_UPLOAD_BASE_ENDPOINT + "/upload", requestOptions)
      .then(async (response) => {
        const isJson = response.headers
          .get("content-type")
          ?.includes("application/json");
        const data = isJson && (await response.json());

        // check for error response
        if (!response.ok) {
          // get error message
          const error = (data && data.message) || response.status;
          setFileUploadResponse(data.message);
          return Promise.reject(error);
        }

        console.log(data.message);
        setFileUploadResponse(data.message);
      })
      .catch((error) => {
        console.error("Error while uploading file!", error);
      });
    setFileUploadProgress(false);
  };


const handleSubmit = async(e) =>{

    e.preventDefault();
    try{
        const response = await axios.post(url, {
          brand: brand,
          model: model,
          serialNumber: serialNumber,
          size: size,
          purchaseDate: purchaseDate,
          purchasePrice: purchasePrice,
          arrivalDate: arrivalDate,

          productCategory: productCategory,

        }); 
        console.log(response.data);
    } catch (error){
        console.log(error.response);
    }
}

return (
  <div className="meuForm">
    <div className="form-row">
      <form onSubmit={fileSubmitHandler}>
        <div className="col-md-6 offset-md-3">
          {/* <Upload></Upload> */}
        </div>

        <div className="col-md-6 offset-md-3">
          <input
            multiple
            onChange={uploadFileHandler}
            type="file"
            className="form-control"
            placeholder="Brand"
            // onChange={(e) => setBrand(e.target.value)}
          />
          {!fileSize && <p style={{ color: "red" }}>File size exceeded!!</p>}
          {fileUploadProgress && (
            <p style={{ color: "red" }}>Uploading File(s)</p>
          )}
          {fileUploadResponse != null && (
            <p style={{ color: "green" }}>{fileUploadResponse}</p>
          )}
        </div>

        <div className="col-md-6 offset-md-3">
          <input
            multiple
            onChange={uploadFileHandler}
            type={"text"}
            name="brand"
            id="brand"
            value={brand}
            className="form-control"
            placeholder="Brand"
            // onChange={(e) => setBrand(e.target.value)}
          />

        </div>
        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="model"
            id="model"
            className="form-control"
            value={model}
            placeholder="Model"
            onChange={(e) => setModel(e.target.value)}
          />
        </div>
        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="serialNumber"
            id="serialNumber"
            className="form-control"
            value={serialNumber}
            placeholder="Serial Number"
            onChange={(e) => setSerialNumber(e.target.value)}
          />
        </div>
        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="size"
            id="size"
            className="form-control"
            value={size}
            placeholder="Size"
            onChange={(e) => setSize(e.target.value)}
          />
        </div>

        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="purchaseDate"
            id="purcaseDate"
            className="form-control"
            value={purchaseDate}
            placeholder="Purcase date"
            onChange={(e) => setPurchaseDate(e.target.value)}
          />
        </div>

        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="purchasePrice"
            id="purchasePrice"
            className="form-control"
            value={purchasePrice}
            placeholder="Purcase price"
            onChange={(e) => setPurchasePrice(e.target.value)}
          />
        </div>

        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="arrivalDate"
            id="arrivalDate"
            className="form-control"
            value={arrivalDate}
            placeholder="Arrival date"
            onChange={(e) => setArrivalDate(e.target.value)}
          />
        </div>
        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="productCategory"
            id="productCategory"
            className="form-control"
            value={productCategory}
            placeholder="Produto categoria"
            onChange={(e) => setProductCategory(e.target.value)}
          />
        </div>

        <div className="col-md-6 offset-md-3">
          <button
            type="submit"
            className="btn btn-success"
            onChange={(e) => this.handleSubmit(e)}
          >
            Salvar
          </button>
        </div>
      </form>
    </div>
  </div>
);
};
export default App;

