  },
  function (e) {
    alert("Error submitting form!");
  }
);
}

return (
  <div className="meuForm">
    <div className="form-row">
      <form onSubmit={handleSubmit}>
        <div className="col-md-6 offset-md-3">
          {/* <Upload></Upload> */}

        <div className="col-md-6 offset-md-3">
          <input
            type={file}
            name="file"
            id="file"
