            className="form-control"
            placeholder="Brand"
            onChange={(e) => setBrand(e.target.value)}
          />
        </div>
        <div className="col-md-6 offset-md-3">
          <input
            type={"text"}
            name="model"
            id="model"
            className="form-control"
            value={model}
            placeholder="Model"
            onChange={(e) => setModel(e.target.value)}
          />
        </div>
        <div className="col-md-6 offset-md-3">
