      alert("Oops! ");
    }
  },
  function (e) {
    alert("Error submitting form!");
  }
);


}
render(){
  return (
    <div className="Upload">
      <h2>FORM</h2>

      <form>
        <div className="">
          <label>FILE</label>
          <input type="file" name="file" onChange={(e) => this.handleFile(e)} />


          </div>
      </form>

      <button type="button" onClick={(e) => this.handleUpload(e)}>
        enviar
      </button>
    </div>
  );
}
}


export default Upload;
